def connect():    import network    import machine    import time    led2 = machine.Pin(2, machine.Pin.OUT)    led2.on()    led16 = machine.Pin(16, machine.Pin.OUT)    sta_if = network.WLAN(network.STA_IF)    if not sta_if.isconnected():        print('connecting to network...')        sta_if.active(True)        # sta_if.connect('Virus', '4ilivili')        sta_if.connect('Netstream', 'BEEFDEADBEEADEAD')        while not sta_if.isconnected():            led16.off()            time.sleep(1)            led16.on()            time.sleep(1)            pass    print('network config:', sta_if.ifconfig())    led2.off()    time.sleep(0.2)    led2.on()def no_debug():    import esp    # you can run this from the REPL as well    esp.osdebug(None)no_debug()connect()