from umqtt.simple import MQTTClientclass Mqtt:    """    Represents an MQTT client which publishes temperature data on an interval    """    def __init__(self, client_id, server, topic):        """        :param client_id: Unique MQTT client ID        :param server: MQTT broker domain name / IP        :param topic: Topic to publish        """        self.client = MQTTClient(client_id, server)        self.topic = topic        self.client.connect()    def publish(self, message):        self.client.publish(self.topic, str(message))