import ubinasciiimport jsonfrom micropyserver import MicroPyServerfrom machine import unique_id, Pin, I2Cfrom my_lib import bme280from connect import Connectfrom led_pwm import LedPwmled = LedPwm(2)i2c = I2C(scl=Pin(5), sda=Pin(4))config = json.loads(open('config.json').read())client_id = ubinascii.hexlify(unique_id())# Connect to wifiConnect.check_and_reconnect()server = MicroPyServer()''' add request handler '''def show_message(request):    """ request handler """    led.blink_duty(2, 0.1, 1010)    bme = bme280.BME280(i2c=i2c, address=0x76)    temp = bme.values[0]    hum = bme.values[2]    pres = bme.values[1]    html = """<html><head> <title>Temp, Hum & Pres</title>     <meta http-equiv="Content-Type" content="text/html; charset=utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <link rel="icon" href="data:,">     <style>    html{font-family: Helvetica; display:inline-block; margin: 0px auto; text-align: center;}    h1{color: #0F3376; padding: 2vh;}p{font-size: 1.5rem;}    .fixed {position: fixed;width: 50%;bottom: 10px;}     </style>    </head><body> <h1>Temp, Hum & Pres</h1>     <p>Температура: <strong>""" + str(temp) + """</strong></p>    <p>Влажность: <strong>""" + str(hum) + """</strong></p>    <p>Давление: <strong>""" + str(pres) + """</strong></p>    <div class="fixed"><strong>Z nt,z k.,k. =)</strong></div>    </body></html>"""    server.send(response=html, content_type="Content-Type: text/html")server.add_route("/", show_message)''' start server '''server.start()